package firstExcercise;

public class OddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 5;
		int output = i%2;
					
		if (output == 0) {
			System.out.println("Even Number");
			
		} else {
			System.out.println("Odd Number");
		}

	}

}
